// Creamos un archivo PetContext.js
import React, { useState, createContext, useContext } from 'react';
import regularfondo from "../images/fondo-images/fondobasico2.jpg";

const FondoContext = createContext();

// Hook personalizado 
// Cualquier componente que llame a usePet recibira los valores actuales de petName y setPetName
export function useFondo() {
  return useContext(FondoContext);
}

// Componente 
export const FondoProvider = ({ children }) => {
  
  const [fondoImage, setFondoImage] = useState(regularfondo);

  return (
    <FondoContext.Provider value={{ fondoImage, setFondoImage }}>
      {children}
    </FondoContext.Provider>
  );
};
